  computed: {
    disabledNext() {
      if (this.step === 1 && !this.form.type) return true
      if (this.step === 2 && (!this.form.degree || !this.form.country))
        return true
      if (this.step === 3 && (!this.form.university || !this.form.campus))
        return true
      return false
    },
  },
  created() {
    this.getUniversities()
  },
  methods: {
    handleUniversityChange(value) {
      this.table.forEach((stroke) => {
        if (stroke['Название университета'] === value)
          this.price = stroke['Стоимость (от…)']
      })
    },
    getUniversities() {
      this.table = costJson.pvt
      this.universities = this.table.map(
        (stroke) => stroke['Название университета']
      )
    },
    submitForm() {
      this.$root.$emit('openRequest', this.form)
      this.closeModal()
    },
    reTry() {
      this.step = 1
      this.form = {
        type: null,
      }
    },
    openModal() {
      this.step = 1
      this.form = {
        type: null,
      }
      this.showModal = true
    },
    closeModal() {
      this.showModal = false
      this.step = 1
      this.form = {
        type: null,
      }
    },
    nextStep() {
      if (this.step === 4 || this.disabledNext) return
      this.step++
    },
    prevStep() {
      if (this.step === 1) return
      this.step--
    },
  },
}
