<script>
export default {
  props: {
    isError: {
      type: Boolean,
      default: false,
    },
  },
  data: () => ({
    menu_modal: false,
    success_modal: false,
    request_modal: false,
    is_transparent: true,
    form: {},
  }),
  mounted() {
    this.onScroll()
    window.addEventListener('scroll', this.onScroll)
    this.$root.$on('openRequest', () => {
      this.openModal()
    })
  },
  methods: {
    closeMenu() {
      this.menu_modal = false
    },
    openMenu() {
      this.menu_modal = true
    },
    openPolicy() {
      this.$root.$emit('openPolicy')
    },
    async submitForm() {
      if (!this.$refs.form.validate()) return

      await this.$axios
        .$post(``, this.form)
        .then(() => {
          this.success_modal = true
        })
        .catch((err) => {
          const error = Object.values(err.response.data).join(', ')
          alert(error)
        })
    },
    closeModal() {
      this.request_modal = false
      this.success_modal = false
    },
    openModal() {
      this.request_modal = true
    },
    onScroll() {
      const pos = window.scrollY
      if (pos > 100) this.is_transparent = false
      else this.is_transparent = true
    },
    setLocale(lang) {
      this.$root.$i18n.locale = lang
    },
  },
}
</script>

